mapper.update:
UPDATE notice 
SET readcount = readcount + 1 
WHERE num = #{num}

UPDATE event 
SET e_section = #{ESection}, e_title = #{ETitle} , start_date = #{startDate} , end_date = #{endDate}  
WHERE e_num = #{ENum}


UPDATE notice 
SET re_seq = re_seq + 1 
WHERE re_ref = #{reRef} 
AND re_seq > #{reSeq}

mapper.select:
SELECT COUNT(*) 
FROM event 
WHERE e_section LIKE '영화';

SELECT *
FROM event 
WHERE e_section LIKE '영화';

SELECT COUNT(*) 
FROM event 
WHERE e_section LIKE '시사회/무대인사';

SELECT *
FROM event 
WHERE e_section LIKE '시사회/무대인사';

