mapper.insert:
UPDATE notice 
SET total_count = #{totalCount} 
WHERE num = #{num}

mapper.update:
UPDATE notice 
SET readcount = readcount + 1 
WHERE num = #{num}

UPDATE notice 
SET subject = #{subject}, content = #{content} 
WHERE num = #{num}


UPDATE notice 
SET re_seq = re_seq + 1 
WHERE re_ref = #{reRef} 
AND re_seq > #{reSeq}

UPDATE notice
SET likes = likes + 1 
WHERE num = #{num}

UPDATE notice
SET likes = likes - 1
WHERE num = #{num}
